#appModules/mmc.py#A part of NonVisual Desktop Access (NVDA)#Copyright (C) 2006-2012 NVDA Contributors#This file is covered by the GNU General Public License.#See the file COPYING for more details.import appModuleHandlerimport apiimport controlTypesimport uiclass AppModule(appModuleHandler.AppModule):	__gestures = {		"kb:rightarrow": "getcurrentobject",		"kb:leftarrow": "getcurrentobject",		"kb:uparrow": "getcurrentobject",		"kb:downarrow": "getcurrentobject"	}	def script_getcurrentobject(self, gesture):		gesture.send()		res = -1		xn = -1		focus = api.getFocusObject()		if focus.role == controlTypes.ROLE_TABLE:			for x in focus.children:				xn += 1				if controlTypes.STATE_SELECTED in x.states:					res = xn			try:				ui.message(focus.children[res].name+' '+focus.children[res].value)			except IndexError: 				pass